sites.css:
:root 
{
    --main-color:#0066ff !important;
    --hover-color:#003399 !important;
    --blur-nsfw:15px !important;
	--background-blur: 0px !important;
	--background-brightness: 0% !important;
	--rs-spacing: 1px;
	--rs-popout-spacing: 1px;
	--rs-profile-spacing: 1px;
    --rs-width: 2px; /* width of the status */    --rs-popout-spacing: 3px; /* spacing between avatar and status for user popouts */
    --rs-popout-width: 4px; /* width of the status for user popouts */
    --rs-profile-width: 4px; /* width of the status on modals/profiles */
    --rs-online-color: #43b581; /* color for online status */
    --rs-idle-color: #faa61a; /* color for idle status */
    --rs-dnd-color: #f04747; /* color for dnd status */
    --rs-offline-color: #636b75; /* color for offline status */
    --rs-invisible-color: #747f8d; /* color for invisible status - Note this will only show for your own invisibility */
    --rs-streaming-color: #643da7; /* color for streaming status */
}

.app {
    --radial-status: 1;
}

.wrapper-2F3Zv8 [class*="status"],
[class*="memberOffline-"] [class*="avatarOffline-"]::after {
    width:100%!important;
    background: transparent;
    border-radius: inherit !important;
    height: 100%!important;
    transition: all 200ms ease !important;
	top: calc(-1*var(--rs-spacing) + -1*var(--rs-width)) !important;
	right: calc(-1*var(--rs-spacing) + -1*var(--rs-width)) !important;
	bottom: calc(-1*var(--rs-spacing) + -1*var(--rs-width)) !important;
	left: calc(-1*var(--rs-spacing) + -1*var(--rs-width)) !important;
	padding: var(--rs-spacing) !important;
	transition: all 200ms ease !important;
	border: var(--rs-width) solid #636b75 !important;
    margin: 0 !important;
    pointer-events: none;
}

[class*="user-info"] [class*="status"] {
	border: none !important;
}

[class*="theme-"] .wrapper-2F3Zv8 [class*="idle"],
[class*="theme-"] .wrapper-2F3Zv8 [class*="Idle"],
.root-2sNHUF [class*="idle"] {
    border-color: var(--rs-idle-color) !important;
}

[class*="theme-"] .wrapper-2F3Zv8 [class*="dnd"],
[class*="theme-"] .wrapper-2F3Zv8 [class*="Dnd"],
.root-2sNHUF [class*="dnd"] {
    border-color: var(--rs-dnd-color) !important;
}

[class*="theme-"] .wrapper-2F3Zv8 [class*="streaming"],
[class*="theme-"] .wrapper-2F3Zv8 [class*="Streaming"],
.root-2sNHUF [class*="streaming"]{
    animation: streaming-flash 1.5s linear infinite !important;
    border-color: var(--rs-streaming-color) !important;
}

[class*="theme-"] .wrapper-2F3Zv8 [class*="online"],
[class*="theme-"] .wrapper-2F3Zv8 [class*="Online"],
.root-2sNHUF [class*="online"] {
    border-color: var(--rs-online-color) !important;
}

[class*="theme-"] .wrapper-2F3Zv8 [class*="invisible"],
[class*="theme-"] .wrapper-2F3Zv8 [class*="Invisible"],
.root-2sNHUF [class*="invisible"] {
    border-color: var(--rs-invisible-color) !important;
}

[class*="theme-"] .wrapper-2F3Zv8 [class*="offline"],
[class*="theme-"] .wrapper-2F3Zv8 [class*="Offline"],
.root-2sNHUF [class*="offline"] {
    border-color: var(--rs-offline-color) !important;
}

[class*="members-"] [class*="memberOffline-"] [class*="status"] {
	display: flex !important;
}

[class*="members-"] [class*="member-"] {
	padding-bottom: calc((var(--rs-spacing) + var(--rs-width))*2) !important;
	padding-top: calc((var(--rs-spacing) + var(--rs-width))*2) !important;
}

[class*="memberOffline-"] [class*="avatarOffline-"]::after {
	content: "";
	position: absolute;
}

.wrapper-2F3Zv8 [class*="status"][class*="typing"] {
    width: 100% !important;
    background-color: rgba(0, 0, 0, 0.4) !important;
	right: 0 !important;
}
.wrapper-2F3Zv8 [class*="status"][class*="typing"] [class*="spinner"] [class*="inner"] {
    left: 1px !important;
    top: 1px !important;
}

.wrapper-2F3Zv8 [class*="mask"],
[class*="avatarWrapper-"] [class*="mask"] {
	-webkit-mask: none!important;
	mask: none;
}

[class*="theme-"] .userPopout-11hFKo .wrapper-2F3Zv8,
[class*="theme-"] .root-2sNHUF .wrapper-2F3Zv8 {
    border: none;
}

[class*="theme-"] .userPopout-11hFKo .avatar-1BXaQj.popout-UKvsJt [class*="status"],
[class*="theme-"] .root-2sNHUF .header-2Lg0Oe .wrapper-2F3Zv8 [class*="status"] {
	border-width: var(--rs-popout-width) !important;
    top: calc(-1*var(--rs-popout-spacing) + -1*var(--rs-popout-width)) !important;
    right: calc(-1*var(--rs-popout-spacing) + -1*var(--rs-popout-width)) !important;
    bottom: calc(-1*var(--rs-popout-spacing) + -1*var(--rs-popout-width)) !important;
    left: calc(-1*var(--rs-popout-spacing) + -1*var(--rs-popout-width)) !important;
	padding: var(--rs-popout-spacing) !important;
	-webkit-box-shadow: none !important;
	box-shadow: none !important;
	box-sizing: content-box;
}

[class*="theme-"] .root-2sNHUF .header-2Lg0Oe .wrapper-2F3Zv8 [class*="status"] {
    border-width: var(--rs-profile-width) !important;
    padding: var(--rs-profile-spacing) !important;
    top: calc(-1*var(--rs-profile-spacing) + -1*var(--rs-profile-width)) !important;
    right: calc(-1*var(--rs-profile-spacing) + -1*var(--rs-profile-width)) !important;
    bottom: calc(-1*var(--rs-profile-spacing) + -1*var(--rs-profile-width)) !important;
    left: calc(-1*var(--rs-profile-spacing) + -1*var(--rs-profile-width)) !important;
}

[class*="theme-"] .root-2sNHUF .wrapper-2F3Zv8 > a {
	width: inherit;
    height: inherit;
	border-radius: inherit;
    position: absolute;
    z-index: 1;
}

	/* Jumbo Avatars */
.emoji
{
	width: 35px;
	height: 35px;
}
.emoji.jumboable 
{ 
	width: 50px; 
	height: 50px;
	margin-top: 0;
}

	/* Square Avatars */
.image-33JSyf,
.guildInner-3DSoA4,
.avatar-large, 
.avatar-profile, 
.avatar-small, 
.avatar-xlarge, 
.avatar-xsmall, 
.avatar-xxlarge,
.avatarHint-1qgaV3,
.wrapper-2F3Zv8
{
    border-radius: 0 !important;
}
.mask-3OgeRz 
{
    -webkit-mask: none !important;
    mask: none !important;
}

	/* Remove Message Borders */
.containerCozy-jafyvG .divider-32i8lo
{
	border-bottom: 0
}

	/* Blue Mention Sign */
.isMentionedCozy-3isp7y:after
{
	background: rgba(0, 153, 255, 0.1);
	border-left: 4px solid #0099ff
}
.theme-dark .isMentioned-N-h9aa
{
	background: rgba(0, 153, 255, 0.1)
}

	/* Underline Roles in Member List & DMs */
.members-1998pB .membersGroup-v9BXpm, .private-channels header 
{
	text-align: center;
	overflow: visible;
	margin-bottom: 10px;
}
.members-1998pB .membersGroup-v9BXpm::after, .private-channels header::after 
{
	content: "";
	display: block;
	height: 1px;
	width: 100%;
	border-bottom: 2px solid #0099ff;
}
.private-channels header::after {padding-bottom: 10px;}

	/* Underline Labels in Channel List */
.channels-Ie2l6A .wrapperDefault-10Jfvz, .channels-Ie2l6A .wrapperHovered-28fu1D 
{
	padding-top: 28px;
	padding-right: 8px;
	padding-left: 18px;
	padding: 0 0 8px 0;
	height: auto;
	margin: 0;
	border-bottom: 2px solid #0099ff;
}
.channels-Ie2l6A .nameDefault-2DI02H, .channels-Ie2l6A .nameHovered-1gxhWH 
{
	text-align: center;
}
